import AuthenticationService from "../Service/AuthenticationService";
import Form from "./Form";
import {useState} from "react";
import "../css/registrationForm.css";
import {useNavigate} from "react-router-dom";
import {Redirect} from "react-router";

const LoginForm = (props) => {
    const navigate = useNavigate();
    const userInfo = localStorage.getItem("user");
    let isAuthenticated = false;
    if(userInfo !== null) isAuthenticated = true;

    const [values, setValues] = useState({
        username: "",
        password: "",
    });

    const inputs = [
        {
            id: 1,
            name: "username",
            type: "text",
            placeholder: "Имя пользователя",
            errorMessage:
                "Пожалуйста, введите корректное имя пользователя.",
            label: "Имя пользователя",
            pattern: "^[A-Za-z0-9]{3,16}$",
            required: true,
        },
        {
            id: 2,
            name: "password",
            type: "password",
            placeholder: "Пароль",
            errorMessage:"Пожалуйста, введите корректный пароль.",
            label: "Пароль",
            pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])[a-zA-Z0-9!@#$%^&*-]{8,20}$`,
            required: true,
        },
    ];

    const handleSubmit = async (e) => {
        e.preventDefault();
        // AuthenticationService.login(e.target.username.value, e.target.password.value).then((res) => {
        //     navigate("/success", { replace: true });
        // });
        const user = e.target.username.value;
        const pass = e.target.password.value;
        const response = await AuthenticationService.login(user, pass);
        const roles = response.data;
        localStorage.setItem("user", JSON.stringify({user, pass, roles}));
        console.log(localStorage.getItem("user"));
        navigate("/success", {replace: true});
    };

    const onChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

    return ( isAuthenticated ? <Redirect to="/success"/> :
        <Form onSubmit={handleSubmit}
              onChange={onChange}
              inputs={inputs}
              values={values}
              title="Авторизация"
              buttonTitle="Войти"
        >
        </Form>
    );
};

export default LoginForm;
