import AuthenticationService from "../Service/AuthenticationService";
import Form from "./Form";
import { useState } from "react";
import "./registrationForm.css";

const RegistrationForm = (props) => {
    const [values, setValues] = useState({
        username: "",
        password: "",
        confirmPassword: "",
    });

    const inputs = [
        {
            id: 1,
            name: "username",
            type: "text",
            placeholder: "Имя пользователя",
            errorMessage:
                "В имени пользователя должно быть от 3 до 16 символов, " +
                "и имя не должно содержать специальные символы!",
            label: "Имя пользователя",
            pattern: "^[A-Za-z0-9]{3,16}$",
            required: true,
        },
        {
            id: 2,
            name: "password",
            type: "password",
            placeholder: "Пароль",
            errorMessage:
                "Длина пароля должна быть от 8 до 20 символов, и " +
                "пароль должен содержать как минимум 1 букву, 1 цифру и " +
                "1 специальный символ !",
            label: "Пароль",
            pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])[a-zA-Z0-9!@#$%^&*-]{8,20}$`,
            required: true,
        },
        {
            id: 3,
            name: "confirmPassword",
            type: "password",
            placeholder: "Подтвеждение пароля",
            errorMessage: "Пароли не совпадают!",
            label: "Подтверждение пароля",
            pattern: values.password,
            required: true,
        },
    ];

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(e.target.username.value);
        console.log(e.target.password.value);
        AuthenticationService.register(e.target.username.value, e.target.password.value).then(() => {
        });

    };

    const onChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

    return (
        <Form onSubmit={handleSubmit}
              onChange={onChange}
              inputs={inputs}
              values={values}>
        </Form>
    );
};

export default RegistrationForm;
