import "./upload.css";
import React, {useState} from "react";
import loginForm from "./LoginForm";
import axios from "axios";

const SuccessPage = (props) => {
    const [drag, setDrag] = useState(false);

    function dragStartHandler(event) {
        event.preventDefault();
        setDrag(true);
    }

    function dragLeaveHandler(event) {
        event.preventDefault();
        setDrag(false);
    }

    function dropHandler(event) {
        event.preventDefault();
        let files = [...event.dataTransfer.files];
        const formData = new FormData();
        const auth = JSON.parse(localStorage.getItem("user"));
        console.log(auth.user);
        formData.append("file", files[0]);
        formData.append("user", auth.user);
        formData.forEach(event => console.log(event));
        console.log(true);
        console.log(files);
        // axios.post('http://localhost:8080/api/v1/registration/add', formData).then(res => console.log(res));
        axios.get('http://localhost:8080/api/v1/registration/testaxios').then(res =>
        console.log(res))
        setDrag(false);
    }

    return (
        <div className="container">
            {
                drag
                    ?
                    <div className="drop-area"
                           onDragStart={event => dragStartHandler(event)}
                           onDragLeave={event => dragLeaveHandler(event)}
                           onDragOver={event => dragStartHandler(event)}
                    onDrop={event => dropHandler(event)}>
                        Отпустите файлы для загрузки на сервер
                    </div>
                    : <div
                            onDragStart={event => dragStartHandler(event)}
                            onDragLeave={event => dragLeaveHandler(event)}
                            onDragOver={event => dragStartHandler(event)}
                    >Перетащите необходимые файлы</div>
            }
        </div>
    );
};

export default SuccessPage;
