import React, {useState} from "react";
import axios from "axios";

const UploadFile = ({images, setImages}) => {
    const [drag, setDrag] = useState(false);

    function dragStartHandler(event) {
        event.preventDefault();
        setDrag(true);
        console.log("start");
        console.log(event.target);
    }

    function dragLeaveHandler(event) {
        setDrag(false);
        console.log("leave");
        console.log(event.target);
    }

    async function dropHandler(event) {
        event.preventDefault();
        setDrag(false);
        let files = [...event.dataTransfer.files];
        const formData = new FormData();
        const auth = JSON.parse(localStorage.getItem("user"));
        console.log(auth.user);
        formData.append("file", files[0]);
        formData.append("user", auth.user);
        formData.forEach(event => console.log(event));
        console.log(true);
        console.log(files);
        await axios.post('http://localhost:8080/api/v1/registration/add', formData,{
                auth: {
                    username: auth.user,
                    password: auth.pass
                }
            }
        ).then(res => console.log(res));

       const response = await axios.get('http://localhost:8080/api/v1/registration/add', {
            auth: {
                username: auth.user,
                password: auth.pass
            }
        });

       const images = response.data;
       console.log(images);
       setImages(images);


    }
    return (
        <>
            <div className="container">
                {
                    drag
                        ?
                        <div className="drop-area"
                             onDragStart={event => dragStartHandler(event)}
                             onDragLeave={event => dragLeaveHandler(event)}
                             onDragOver={event => console.log("over")}
                             onDrop={event => dropHandler(event)}>
                            <svg className="box__icon" xmlns="http://www.w3.org/2000/svg" width="50" height="43"
                                 viewBox="0 0 50 43">
                                <path
                                    d="M48.4 26.5c-.9 0-1.7.7-1.7 1.7v11.6h-43.3v-11.6c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v13.2c0 .9.7 1.7 1.7 1.7h46.7c.9 0 1.7-.7 1.7-1.7v-13.2c0-1-.7-1.7-1.7-1.7zm-24.5 6.1c.3.3.8.5 1.2.5.4 0 .9-.2 1.2-.5l10-11.6c.7-.7.7-1.7 0-2.4s-1.7-.7-2.4 0l-7.1 8.3v-25.3c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v25.3l-7.1-8.3c-.7-.7-1.7-.7-2.4 0s-.7 1.7 0 2.4l10 11.6z"/>
                            </svg>
                            <div>Отпустите файлы для загрузки на сервер</div>
                        </div>
                        : <div className="not-drop"
                               onDragStart={event => dragStartHandler(event)}
                               onDragLeave={event => dragLeaveHandler(event)}
                               onDragOver={event => dragStartHandler(event)}
                        ><svg className="box__icon" xmlns="http://www.w3.org/2000/svg" width="50" height="43"
                              viewBox="0 0 50 43">
                            <path
                                d="M48.4 26.5c-.9 0-1.7.7-1.7 1.7v11.6h-43.3v-11.6c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v13.2c0 .9.7 1.7 1.7 1.7h46.7c.9 0 1.7-.7 1.7-1.7v-13.2c0-1-.7-1.7-1.7-1.7zm-24.5 6.1c.3.3.8.5 1.2.5.4 0 .9-.2 1.2-.5l10-11.6c.7-.7.7-1.7 0-2.4s-1.7-.7-2.4 0l-7.1 8.3v-25.3c0-.9-.7-1.7-1.7-1.7s-1.7.7-1.7 1.7v25.3l-7.1-8.3c-.7-.7-1.7-.7-2.4 0s-.7 1.7 0 2.4l10 11.6z"/>
                        </svg>
                            Перетащите необходимые файлы</div>
                }
            </div>
        </>
    );
};

export default UploadFile;
