import React, { useState } from "react";
import "./formInput.css";
import axios from "axios";
import loginForm from "./LoginForm";

const ImageList = ({images, setImages}) => {
    const staticUrl = "http://localhost:8080/img/";

    async function deleteImage(event) {
        event.preventDefault();

        const auth = JSON.parse(localStorage.getItem("user"));
        const [url] = event.target.classList
        console.log(url);
        const formData = new FormData();
        formData.append("url", url);
        await axios.post('http://localhost:8080/api/v1/registration/delete', formData,{
                auth: {
                    username: auth.user,
                    password: auth.pass
                }
            }
        ).then(response => console.log(response));

        const response = await axios.get('http://localhost:8080/api/v1/registration/add', {
            auth: {
                username: auth.user,
                password: auth.pass
            }
        });

        const images = response.data;
        console.log(images);
        setImages(images);
        return undefined;
    }

    const listItems = images.map((image) => {
        return <div className="image">
            <img src={staticUrl.concat(image.url)} alt={image.url}/>
            <button className={image.url} onClick={(event => deleteImage(event))}>Удалить</button>
        </div>
        }
    );

    return (
        <div className="imageList">
            {listItems}
        </div>
    );
};

export default ImageList;
