import AuthenticationService from "../Service/AuthenticationService";
import Form from "./Form";
import { useState } from "react";
import "./registrationForm.css";
import { useNavigate } from "react-router-dom";

const LoginForm = (props) => {
    let navigate = useNavigate();

    const [values, setValues] = useState({
        username: "",
        password: "",
    });

    const inputs = [
        {
            id: 1,
            name: "username",
            type: "text",
            placeholder: "Имя пользователя",
            errorMessage:
                "Пожалуйста, введите корректное имя пользователя.",
            label: "Имя пользователя",
            pattern: "^[A-Za-z0-9]{3,16}$",
            required: true,
        },
        {
            id: 2,
            name: "password",
            type: "password",
            placeholder: "Пароль",
            errorMessage:"Пожалуйста, введите корректный пароль.",
            label: "Пароль",
            pattern: `^(?=.*[0-9])(?=.*[a-zA-Z])[a-zA-Z0-9!@#$%^&*-]{8,20}$`,
            required: true,
        },
    ];

    const handleSubmit = (e) => {
        e.preventDefault();
        // AuthenticationService.login(e.target.username.value, e.target.password.value).then((res) => {
        //     navigate("/success", { replace: true });
        // });
        const response = AuthenticationService.login(e.target.username.value, e.target.password.value);
        console.log(response);
    };

    const onChange = (e) => {
        setValues({ ...values, [e.target.name]: e.target.value });
    };

    return (
        <Form onSubmit={handleSubmit}
              onChange={onChange}
              inputs={inputs}
              values={values}
              title="Авторизация"
              buttonTitle="Войти"
        >
        </Form>
    );
};

export default LoginForm;
